class GoodsItem{constructor(t,e){this.title=t,this.price=e}render(){return`<div class="goods-item">\n\t<h3>${this.title}</h3>\n\t<div class="goods-img"></div>\n\t<p>${this.price}<button>Добавить</button></p>\n\t</div>`}}class GoodsList{constructor(){this.goods=[]}fetchGoods(){this.goods=[{title:"Shirt",price:150},{title:"Socks",price:50},{title:"Jacket",price:350},{title:"Shoes",price:250}]}render(){let t="";this.goods.forEach(({title:e,price:s})=>{const o=new GoodsItem(e,s);t+=o.render()}),document.querySelector(".goods-list").innerHTML=t}}class BasketItem{constructor(t,e){this.title=t,this.price=e}render(){return`<div class="basket-item">\n\t<h3>${this.title}</h3>\n\t<div class="basket-img"></div>\n\t<p>${this.price}<button>+</button><button>-</button></p>\n\t</div>`}}class GoodsList{constructor(){this.goods=[]}addBasketItem(t){this.goods.push(t)}sumBasketPrice(){let t=0;this.goods.forEach(({price:e})=>{t+=e}),document.querySelector(".basket-sum").innerText="Сумма: "+t}render(){let t="";this.goods.forEach(({title:e,price:s})=>{const o=new BasketItem(e,s);t+=o.render()}),document.querySelector(".basket-list").innerHTML=t}}const list=new GoodsList;list.fetchGoods(),list.render();